INSERT INTO java_bean.role (id, name, description) VALUES (1, 'Application Assembler', 'Использует различные развернутые компоненты в специфической среде для создания полного приложения.');
INSERT INTO java_bean.role (id, name, description) VALUES (2, 'Deployer', 'Для EJB, развертывание состоит в процессе установки одного или нескольких EJB компонентов в специфическом EJB Контейнере. Deployer является экспертом в специфической рабочей среде и отвечает за связывание EJB со всеми ресурсами, которые ему нужны для работы (соединение с базой данных, таблицы, другие EJB и тому подобное).');
INSERT INTO java_bean.role (id, name, description) VALUES (3, 'Enterprise Bean Provider', 'Реализует EJB компонент и оформляет его для распространения. Знает о прикладной области, но может не знать об операционной среде, в которой компонент будет использоваться.');
INSERT INTO java_bean.role (id, name, description) VALUES (4, 'System Administrator', 'Он отвечает за создание и поддержку пользователей, баз данных и за общую инфраструктуру ресурсов, необходимых для специфической рабочей среды.');